<?php//  用于登录验证码检测header('Content-Encoding: plain');//不使用压缩。因为服务器开启了Gzip，IE6的XMLhttprequest对象不自动解压，导致返回数据错误（就是IE6下面getJSON的callback不执行）。session_start();$verify_code = trim($_REQUEST['verify_code']);if(!$verify_code){    jsonBack(array('code'=>'01','msg'=>'请输入验证码'));}if($verify_code===$_SESSION['chk_code']){    $_SESSION['login_error_times']=0;    unset($_SESSION['chk_code']);    jsonBack(array('code'=>'10','msg'=>'验证码输入正确'));}else{    unset($_SESSION['chk_code']);    jsonBack(array('code'=>'01','msg'=>'验证码输入错误'));}////////////function jsonBack($data){    echo $_GET['callback']."(".json_encode($data).")";    exit();}?>